import React from "react"
import Header from "./components/Header"
import Users from "./components/Users"
import AddUser from "./components/AddUser"

const baseUrl = "https://reqres.in/api/users?page=1"

class App extends React.Component{
  constructor(props) {
    super(props)

    this.state = {
        users: [
        {
          id: 1,
          first_name: 'Арагорн',
          last_name: '3000' ,
          avatar: 'https://cdnn21.img.ria.ru/images/97105/21/971052190_162:0:833:671_1920x0_80_0_0_ab3e3268ab7755701817986b683eeee1.jpg',
          email: 'Индивидуально сильный герой, без ярко выраженной роли, однако можно отнести его к разряду убийц героев.',
          isHappy: true,
        }
      ]
    }
    this.addUser = this.addUser.bind(this)
    this.deleteUser = this.deleteUser.bind(this)
    this.editUser = this.editUser.bind(this)
}
  render(){
    return(<div>
      <Header title="Список пользователей" />
      <main>
          <Users users={this.state.users} onEdit={this.editUser} onDelete={this.deleteUser} />
      </main>
      <aside>
        <AddUser onAdd={this.addUser} /> 
      </aside>
    </div>) 
    }

    deleteUser(id) {
      this.setState({
        users: this.state.users.filter((el) => el.id !== id)
      })
    }

    editUser(user) {
      let allUsers = this.state.users
      allUsers[user.id - 1] = user

      this.setState({users: []}, () => {
        this.setState({users: [...allUsers]})
      })
    }

    addUser(user) {
      const id = this.state.users.length + 1
      this.setState({ users: [...this.state.users, {id, ...user}] })
    }

}

export default App
